  }
  else if($DATA['Id_Estado_Envio']==0){

return '<h6 class="m-0 text-c-blue" style="font-size:10px;"><i class="fas fa-circle text-c-blue f-10 m-r-15"></i>'.$DATA['ESTADO'].'</h6>';
